import React from "react";
import { useForm, useFieldArray } from "react-hook-form";

function WatchFormField() {
  const form = useForm({
    defaultValues: {
      username: "",
      email: "",
      address: {
        houseNo: "",
        state: "",
      },
      phone: ["", ""],
      phNumbers: [{ number: "" }],
      age: 0,
      dob: "",
    },
  });

  const { register, control, handleSubmit, formState } = form;

  const { errors } = formState;

  const { fields, append, remove } = useFieldArray({
    name: "phNumbers",
    control,
  });

  const onFormSubmit = (data) => {
    console.log("Form Submitted!");
    console.log(data); // {"username" : "Vikas", "email" : "vikas@gmail.com"}
  };

  return (
    <div>
      <form onSubmit={handleSubmit(onFormSubmit)}>
        <div>
          <label>List of phone numbers</label>
          <div>
            {fields.map((field, index) => {
              return (
                <div key={field.id}>
                  <input
                    type="text"
                    {...register(`phNumbers.${index}.number`)}
                  />
                  {index > 0 && (
                    <button type="button" onClick={() => remove(index)}>
                      Remove phone number
                    </button>
                  )}
                </div>
              );
            })}
            <button type="button" onClick={() => append({ number: "" })}>
              Add new phone number
            </button>
          </div>
        </div>

        <div>
          <label>Age</label>
          <input
            type="number"
            name="age"
            {...register("age", {
              valueAsNumber: true, // If not set age will be stored as string
            })}
          />
        </div>

        <div>
          <label>DOB</label>
          <input
            type="date"
            name="dob"
            {...register("dob", {
              valueAsDate: true, // If not set age will be stored as string
            })}
          />
        </div>

        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default WatchFormField;
