import React, { useEffect } from "react";
import { useForm, useFieldArray } from "react-hook-form";

function WatchFormField() {
  const form = useForm({
    defaultValues: {
      username: "",
      email: "",
      address: {
        houseNo: "",
        state: "",
      },
      phone: ["", ""],
      phNumbers: [{ number: "" }],
      age: 0,
      dob: "",
    },
  });

  const { register, control, handleSubmit, formState, watch, getValues } = form;

  const { errors } = formState;

  const { fields, append, remove } = useFieldArray({
    name: "phNumbers",
    control,
  });

  const onFormSubmit = (data) => {
    console.log("Form Submitted!");
    console.log(data); // {"username" : "Vikas", "email" : "vikas@gmail.com"}
  };

  const userAge = watch("age");
  const watchAgeDOB = watch(["age", "dob"]);
  const watchAll = watch();

  // Perform sideeffect after watch
  useEffect(() => {
    const sub = watch((value) => {
      console.log("From Console " + value);
    });
    return () => sub.unsubscribe();
  }, [watch]);

  const handleGetValues = () => {
    console.log(getValues());
    console.log(getValues("age"));
    console.log(getValues(["username", "age"]));
  };

  return (
    <div>
      <h2>Watched Age Value - {userAge}</h2>
      {console.log(watchAll)}
      <form onSubmit={handleSubmit(onFormSubmit)}>
        <div>
          <label>Age</label>
          <input
            type="number"
            name="age"
            {...register("age", {
              valueAsNumber: true, // If not set age will be stored as string
            })}
          />
        </div>

        <div>
          <label>DOB</label>
          <input
            type="date"
            name="dob"
            {...register("dob", {
              valueAsDate: true, // If not set age will be stored as string
            })}
          />
        </div>

        <button type="submit">Submit</button>
        <button type="button" onClick={handleGetValues}>
          Get Form Values
        </button>
      </form>
    </div>
  );
}

export default WatchFormField;
