import React, { Fragment, useState } from "react";
import "./List.css";

function List({ data }) {
  const [expandedIndex, setExpand] = useState(-1);

  const handleListExpand = (index) => {
    console.log(index);
    setExpand(index);
  };

  return (
    <div className="list__container">
      <ul className="list__items">
        {data &&
          data.map((d, index) => {
            return (
              <Fragment key={index}>
                <li
                  className="list__item"
                  onClick={() => handleListExpand(index)}
                >
                  {d.title}
                </li>
                <div
                  className="sublist__container"
                  style={{ top: expandedIndex }}
                >
                  {expandedIndex === index ? <List data={d.child} /> : null}
                </div>
              </Fragment>
            );
          })}
      </ul>
    </div>
  );
}

export default List;
